import React from 'react';
import { Link } from 'react-router-dom';
import { FiPlus } from 'react-icons/fi';
import { Map, TileLayer } from 'react-leaflet';
import mapMarker from '../images/Local.svg';
import '../styles/ophanages-map.css';
import 'leaflet/dist/leaflet.css';
import SimpleMap from './SimpleMap'

function OrphanagesMap() {
    return (
        <div id="page-map">
            <aside>
                <header>
                    <img src={mapMarker} alt="Happy"/>

                    <h2>Escolha um orfanato no mapa</h2>
                    <p>Muitas crianças estão esperando a sua visita :)</p>
                </header>

                <footer>
                    <strong>São Paulo</strong>
                    <span>SP</span>
                </footer>
            </aside>

            <Map 
                center={[-23.7208985,-46.7716175]}
                zoom={15}            
                style={{ width: '100%', height: '100%' }}
            >
            {/* <TileLayer url="https://a.tile.openstreetmap.org/{z}/{x}/{y}.png"></TileLayer> */}
                <TileLayer url={`https://api.mapbox.com/styles/v1/mapbox/light-v10/tiles/256/{z}/{x}/{y}@2x?access_token=${process.env.REACT_APP_MAPBOX_TOKEN}`}></TileLayer>
            </Map>            

            <Link to="" className="create-orphanage">
                <FiPlus size="32" color="#FFF"/>
            </Link>            
        </div>
    )
}

export default OrphanagesMap;
